import { Box, Grid } from "@mui/material";
import Container from "react-bootstrap/esm/Container";

export const BlogCard = ({ title, image_url }) => {
    return (
      <Grid container spacing={2} justifyContent="center">
        <Grid item>
          <Container className="blog-box-container" style={{ padding: 0 }}>
            <Box
              className="blog-box"
              sx={{
                width: 350,
                maxWidth: '100%',
                borderRadius: 12,
                overflow: 'hidden',
                boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                marginBottom: 0, // Remove extra space
              }}
            >
              <div
                className="blog-card"
                style={{
                  backgroundImage: `url(${image_url})`,
                  backgroundSize: "cover",
                  backgroundPosition: "center",
                  height: 300,
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "flex-end",
                  padding: 16,
                  borderRadius: 12,
                  color: "#fff",
                }}
              >
                {/* Optionally, you could place the title here for an overlay effect */}
              </div>
            </Box>
            <Box
              sx={{
                textAlign: 'left', // Left-align the title text
                marginTop: 1, // Adjust the margin to ensure proper spacing
                paddingLeft: '16px', // Match the padding of the card for alignment
              }}
            >
              <div className="blog-box-container-text">
                <h4 style={{ fontSize: '12px', margin: 0, padding: 0 }}>{title}</h4>
              </div>
            </Box>
          </Container>
        </Grid>
      </Grid>
    );
  };
