import Container from "react-bootstrap/esm/Container"
import { Stack } from "@mui/material";

import {lashkara} from "../assets/imgs/dance-laungh.png"
import {afreen} from "../assets/imgs/dance-afreen.png"
import {iktara} from "../assets/imgs/dance-barse.png"
import {classical} from "../assets/imgs/dance-classical.png"


export const Dance = () => { 
    const cardsData = [
        {
          image: {lashkara},
          title: "Laung Da Lashkara",
        },
        {
          image: {afreen},
          title: "Afreen",
        },
        {
          image: {iktara},
          title: "Iktara",
        },
        {
          image: {classical},
          title: "Semi-Classical",
        },
      ];

      return (
        <section className="dance" id="dance">
          <Container>
            <h1>dance</h1>
            <span className="wrap">
              I've been a dancer my whole life, and completed my{" "}
              <a href="https://en.wikipedia.org/wiki/Arangetram_(dance)#:~:text=Arangetram%20is%20a%20portmanteau%20of,her%20pupil%20to%20the%20public.">
                bharatnatyam arangetram
              </a>{" "}
              in 2016. I am trained in Bollywood, Garba, Indian folk, and Indian classical dance, and dabble in hip hop and contemporary styles, too. If you are looking for an <b>event choreographer</b> for an upcoming wedding or function, contact me! I have taught dance to kids, college students, young adults, and middle-aged adults, and would be happy to connect. In the meantime, check out some of my work below:
            </span>

            {/* Grid of cards */}
            <div className="card-grid">
            <Stack>
              {cardsData.map((card, index) => (
                <div key={index} className="card">
                  <img src={card.image} />
                  <div className="textbox">
                    <h2>{card.title}</h2>
                  </div>
                </div>
              ))}
              </Stack>
            </div>
          </Container>
        </section>
      );
}
