import { Box } from "@mui/material"


export const BlogCard = ({ title, image_url }) => {
    const backgroundImageStyle = {
      backgroundImage: `url(${image_url})`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      height: "100%",
      width: "100%",
      borderRadius: "10px", // Adjust the border-radius as needed
    };

    return (
      <Box
        height={200}
        width={200}
        my={4}
        display="flex"
        alignItems="center"
        gap={4}
        p={2}
        sx={{ borderRadius: '10px', overflow: 'hidden' }} // Apply borderRadius and overflow: hidden to the Box component
      >
        <div className="blog-card" style={backgroundImageStyle}>
          <div className="blog-txt" style={{ backgroundColor: 'rgba(255, 255, 255, 0.8)', padding: '8px', borderRadius: '0 0 10px 10px' }}>
            <h4>{title}</h4>
          </div>
        </div>
      </Box>
    );
  };
