import { Box, Typography } from "@mui/material";
import Container from "react-bootstrap/esm/Container";

export const BlogCard = ({ title, image_url }) => {
  return (
    <Container style={{ padding: 0, margin: 0 }}> {/* Remove any padding or margin */}
      <Box
        className="blog-box"
        sx={{
          width: 350,
          borderRadius: 12,
          overflow: 'hidden',
          boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
          marginBottom: '0.5rem', // Adjust margin-bottom if needed
        }}
      >
        <div
          style={{
            backgroundImage: `url(${image_url})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            height: 300,
            borderRadius: 12,
          }}
        ></div>
      </Box>
      <Typography 
        sx={{ 
          fontSize: '1rem', 
          color: '#333',
          margin: '0.5rem 0 0 0' // Adjust margins to control spacing
        }}
      >
        {title}
      </Typography>
    </Container>
  );
};
