import { Box, Typography } from "@mui/material";
import Container from "react-bootstrap/esm/Container";

export const BlogCard = ({ title, image_url }) => {
  return (
    <Container>
      <Box
        className="blog-box"
        sx={{
          width: 350,
          borderRadius: 12,
          overflow: 'hidden',
          boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
        }}
      >
        <div
          style={{
            backgroundImage: `url(${image_url})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            height: 300,
            borderRadius: 12,
          }}
        >
        </div>
      </Box>
      <Typography  sx={{fontSize: '1.25rem', fontWeight: 'bold', color: '#333', paddingTop: '0.5em'}}>
        {title
        }</Typography>
      </Container>
  );
};
