import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useState, useEffect } from "react";
import sheilyPic from "../assets/imgs/sheily-profile-pic.png";

export const Banner = () => {
    const fullText = "software engineer, writer, dancer";
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(300 - Math.random() * 75);
    const [isTypingComplete, setIsTypingComplete] = useState(false);

    useEffect(() => {
        let ticker = setInterval(() => {
            tick();
        }, delta);

        return () => {
            clearInterval(ticker);
        };
    }, [text, delta]);

    const tick = () => {
        let updatedText = fullText.substring(0, text.length + 1);
        setText(updatedText);


        if (updatedText === fullText) {
            setIsTypingComplete(true); 
            clearInterval(tick);
        }
    };

    return (
        <section className="banner" id="home">
            <Container>
                <Row className="align-items-center">
                    <Col xs={12} md={6} xl={7}>
                        <div className="content">
                            <h1>{'sheily shah'}</h1>
                            <span className="wrap">{isTypingComplete || !text ? fullText : text}</span>
                        </div>
                    </Col>
                    <Col>
                        <div className="image-container">
                            <img src={sheilyPic} alt="Sheily Shah" className="img-fluid" />
                        </div>
                    </Col>
                </Row>
            </Container>
        </section>
    );
};

export default Banner;
